using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Configuration;
using MoveYourBody.Service.Models;
using MoveYourBody.WebAPI.Controllers;
using System;
using System.Collections.Generic;
using Xunit;

namespace MoveYourBody.WebAPI.Tests
{
    public class CategoriesControllerTest
    {
        //TestDbContext context;
        IConfiguration config;

        public CategoriesControllerTest()
        {
            //Configuration mocking: https://stackoverflow.com/questions/64794219/how-to-mock-iconfiguration-getvalue
            var inMemorySettings = new Dictionary<string, string> {
                {"TopLevelKey", "TopLevelValue"},
                {"Auth:password", "Password"}
            };
            config = new ConfigurationBuilder()
                        .AddInMemoryCollection(inMemorySettings)
                        .Build();
        }
        [Fact]
        public void Get()
        {
            using (var context = TestDbContext.GenerateTestDbContext())
            {
                var sut = new CategoryController(context);
                var result = sut.Get();
                Assert.IsType<OkObjectResult>(result);

                var value = ((OkObjectResult)result).Value;
                List<Category> categories = value.GetPropertyValue<List<Category>>("categories");
                
                Assert.IsType<List<Category>>(categories);
                Assert.Equal(12, categories.Count);
                Assert.Equal(3, categories[2].Id);
                Assert.Equal("Labdarúgás", categories[2].Name);
                Assert.Equal(3, categories[2].Image_id);

                List<Image> images = value.GetPropertyValue<List<Image>>("images");
                Assert.IsType<List<Image>>(images);
                Assert.Equal(12, images.Count);
                Assert.Equal(3, images[2].Id);
                Assert.Equal("", Convert.ToBase64String(images[2].Image_data));
            }
        }
        [Fact]
        public void AddCategory()
        {
            using (var context = TestDbContext.GenerateTestDbContext())
            {
                var sut = new CategoryController(context);
                Category newCategory = new Category()
                {
                    Id = 0,
                    Image_id = 14,
                    Name = "Golf"
                };
                string[] images = new string[] { "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNk+P+/HgAFhAJ/wlseKgAAAABJRU5ErkJggg==", "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAYAAACNbyblAAAAHElEQVQI12P4//8/w38GIAXDIBKE0DHxgljNBAAO9TXL0Y4OHwAAAABJRU5ErkJggg==" };
                var result = sut.AddCategory(newCategory);

                Assert.IsType<OkObjectResult>(result);

                Category value = (Category)((OkObjectResult)result).Value;
                Assert.IsType<Category>(value);
                Assert.Equal(13, value.Id);
                Assert.Equal("Golf", value.Name);
                Assert.Equal(14, value.Image_id);
            }
        }
        
    }
}
